com.etas.jenkins.plugins.CreateTextFile..textFileContent:
user nginx;
worker_processes 4;
pid /run/nginx.pid;


events {
	worker_connections 768;
}

http {
	sendfile on;
	access_log /var/log/nginx/app.log;
	error_log /var/log/nginx/appe.log;
	include /etc/nginx/mime.types;



upstream phpfcgi {
     	server dtalk-php-svc:9000;

     }


  server {

        listen       80;
        server_name  admin.genie.lk;
		root /app/public/;
		error_log    /var/log/nginx/GenieAdminAppSvr1.error.log;
    	access_log   /var/log/nginx/GenieAdminAppSvr1.access.log;
        index index.php index.html index.htm; 

	location / {
        	try_files $uri $uri/ /index.php?$query_string;
    	}

	location ~ \.php$ {
        	try_files $uri /index.php =404;
        	fastcgi_split_path_info ^(.+\.php)(/.+)$;
        	fastcgi_pass phpfcgi;
        	fastcgi_index index.php;
        	fastcgi_param SCRIPT_FILENAME $document_root$fastcgi_script_name;
        	include fastcgi_params;
    	}


        error_page   500 502 503 504  /50x.html;
        location = /50x.html {
            root   html;
        }


    }



}
